var Lightbox={lightboxEl:null};Lightbox.show=function(o){Lightbox.repositionPopups(),this.lightboxEl.addClass("reveal")},Lightbox.close=function(){this.lightboxEl.removeClass("reveal")},Lightbox.repositionPopups=function(){$(".popup").each(function(o){var i=$(this).height(),t=window.innerHeight;$(this).css("top",t/2-i/2)})},window.addEventListener("resize",Lightbox.repositionPopups),window.addEventListener("DOMContentLoaded",function(){Lightbox.lightboxEl=$("#lightbox"),$(".closePopup").on("click",function(){Lightbox.close()})});